/* eslint-disable react/prefer-stateless-function */
import React,{useState} from "react";
import { BrowserRouter } from "react-router-dom";
import P5Wrapper from 'react-p5-wrapper';
import sketch from "./components/sketches/sketchOne.js"



function App() {
  const [location, setLocation] = useState({diningRoom: false, livingRoom: true, bedRoom: false, kitchen: false});
  return (
    <div>
    {location.livingRoom && <P5Wrapper sketch={sketch} />}
    {location.kitchen && <P5Wrapper sketch={sketch} />}
    </div>
  );
}

export default App;
