import React from 'react'

function BookShelf(p, bookShelfImg, posX, posY) {
   
    this.render = function () {
    p.push()
        let offset = posX-bookShelfImg.width
        p.fill(0)
        p.rect(offset, posY, bookShelfImg.width, bookShelfImg.height)
        p.image(bookShelfImg, posX, posY)
    p.pop()
    }
}

export default BookShelf
