/* eslint-disable react/prefer-stateless-function */
import React, { Fragment } from "react";
import User from "../../utils/Stores/User";
import Candle from "../../utils/Stores/Candle";
import { Switch, Route } from "react-router-dom";
import Spinner from 'react-bootstrap/Spinner';
import {
    LoggedInRoute,
    LoggedOutRoute
} from "../index.js";
import {
    NotFound,
    Login,
    Signup,
    Home,
    CandleList,
    CandleCreate
} from "../../pages";

function Main() {
    const [{ pageLoading }] = User.useContext();
    User.refreshOnLoad();
    return (
        <Fragment>
            {pageLoading ? (
                <div className="d-flex justify-content-center mt-5">
                    <Spinner className="mt-5" animation="border" role="status">
                        <span className="sr-only">Loading...</span>
                    </Spinner>
                </div>
            ) : (
                <Candle.Provider>
                    <Switch>
                        <LoggedInRoute exact path="/" component={Home} />
                        <LoggedInRoute exact path="/candle" component={CandleList} />
                        <LoggedInRoute exact path="/candle/create" component={CandleCreate} />
                        <LoggedOutRoute exact path="/login" component={Login} />
                        <LoggedOutRoute exact path="/signup" component={Signup} />
                        <Route path="*" component={NotFound} />
                    </Switch>
                </Candle.Provider>
            )}
        </Fragment>
    );
}

export default Main;
