export default function sketch (p) {
  let rotation = 0;

  p.setup = function () {
    p.createCanvas(p.windowWidth *.75, p.windowHeight*.75);
    console.log(p.windowWidth *.75)
    console.log(p.windowHeight*.75)
    p.background(166, 84, 7);
    p.rectangle();
    p.stroke(0);
    //let twienthiethOfWindow =  (p.windowHeight*.75)/5
    //let backgroundLineYPosition = 20
    let firstBoard = (p.windowHeight*.75)/6
    while(firstBoard<p.windowHeight*.75){
        p.line(0, firstBoard, p.windowWidth *.75,firstBoard);
        firstBoard+=(p.windowHeight*.75)/9
    }
       
    
 
        
    
    
    
   
  };

  p.myCustomRedrawAccordingToNewPropsHandler = function (props) {
    if (props.rotation){
      rotation = props.rotation * Math.PI / 180;
    }
  };

  p.draw = function () {
  
};
};
