export default function sketch (p) {
  let rotation = 0;

  p.setup = function () {
    p.createCanvas(1920, 1080, p.WEBGL);
    p.background(166, 84, 7);
    p.stroke(0);
    p.line(-500, 10, 500, 10);
  };

  p.myCustomRedrawAccordingToNewPropsHandler = function (props) {
    if (props.rotation){
      rotation = props.rotation * Math.PI / 180;
    }
  };

  p.draw = function () {
  
};
};
