export default function sketch (p) {
  let rotation = 0;

  p.setup = function () {
   generateCanvas()
    generateFloor()

    };

 
  p.draw = function () {
  
};

//Making Background 
function backGroundSquare(){
    this.render = function(xpos, ypos, color){
        p.push()
        p.fill(0)
        p.rect(xpos, ypos, p.windowWidth *.75, (p.windowHeight*.75)/35);
        p.pop ()
    }
}

function generateFloor(){
    var square = new backGroundSquare();
    square.render(0, p.windowHeight*.75/6)
}

//Generate Canvas
function generateCanvas(){
    p.createCanvas(p.windowWidth *.75, p.windowHeight*.75);
    p.stroke(0);
    p.background(166, 84, 7);
}
};
